import { useNavigate } from "react-router-dom";
import React, { useState } from "react";
import styled from "styled-components";

// Styled Components
const PageContainer = styled.div`
  position: relative;
  overflow: hidden;
  background: #FFFFFF;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  min-height: 100vh;
  width: 100%;
`;

const Header = styled.div`
  position: absolute;
  background: #F2FFF2;
  height: 89px;
  width: 100%;
  top: 0;
  left: 0;
`;

const Logo = styled.div`
  position: absolute;
  left: 40px;
  top: 29px;
  display: flex;
  align-items: center;
  gap: 5px;
`;

const LogoText = styled.span`
  font-family: ${props => props.font || 'Martel'};
  font-size: ${props => props.size || '24px'};
  font-weight: ${props => props.weight || '900'};
  color: #000000;
`;

const Navigation = styled.div`
  position: absolute;
  height: 50px;
  width: 359.72px;
  left: 50%;
  transform: translateX(-50%);
  top: 27px;
  display: flex;
  gap: 20px;
`;

const NavItem = styled.div`
  padding: 10px;
  cursor: pointer;
  font-family: Inter;
  font-size: 20px;
  font-weight: ${props => props.active ? '700' : '500'};
  color: #282828;
`;

const BrowseJobsButton = styled.div`
  position: absolute;
  right: 40px;
  top: 27px;
  padding: 10px;
  cursor: pointer;
  font-family: Inter;
  font-size: 20px;
  font-weight: 700;
  color: #0C463B;
`;

const FormSection = styled.div`
  margin-top: 120px;
  padding: 20px;
  max-width: 1200px;
  margin-left: auto;
  margin-right: auto;
  position: relative;
`;

const FormTitle = styled.h1`
  font-family: Poppins;
  font-size: 45px;
  font-weight: 600;
  color: #303030;
  margin-bottom: 10px;
`;

const FormSubtitle = styled.p`
  font-family: Poppins;
  font-size: 22px;
  color: #5E6670;
  margin-bottom: 40px;
`;

const FormGroup = styled.div`
  margin-bottom: 30px;
`;

const Label = styled.label`
  display: block;
  font-family: Poppins;
  font-size: 24px;
  font-weight: 500;
  color: #303030;
  margin-bottom: 10px;
`;

const Required = styled.span`
  color: #F04141;
`;

const InputColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-bottom: 20px;
  max-width: 400px;
`;

const InputRow = styled.div`
  display: flex;
  gap: 20px;
  margin-bottom: 20px;
  width: 100%;
`;

const Input = styled.input`
  width: 100%;
  height: 45px;
  border: 2px solid #E7E7F1;
  border-radius: 8px;
  padding: 0 20px;
  font-family: Poppins;
  font-size: 16px;
  color: #121224;
  margin-bottom: 10px;
  
  &::placeholder {
    color: rgba(18, 18, 36, 0.5);
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  height: 100px;
  border: 2px solid #E7E7F1;
  border-radius: 8px;
  padding: 20px;
  font-family: Poppins;
  font-size: 16px;
  color: #121224;
  resize: vertical;
  margin-bottom: 10px;
  
  &::placeholder {
    color: rgba(18, 18, 36, 0.5);
  }
`;

const FormImage = styled.img`
  position: absolute;
  right: 100px;
  top: 40px;
  width: 400px;
  height: auto;
  filter: drop-shadow(0px 10px 40px rgba(0, 0, 0, 0.15));
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  margin-top: 40px;
`;

const Button = styled.button`
  height: 45px;
  width: 200px;
  border-radius: 8px;
  font-family: Inter;
  font-size: 18px;
  font-weight: 700;
  cursor: pointer;
  border: none;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  
  &.primary {
    background: #0C463B;
    color: #FFFFFF;
  }
  
  &.secondary {
    background: #E9191D;
    color: #FFFFFF;
  }
`;

const SkillHobbyContainer = styled.div`
  display: flex;
  gap: 40px;
  margin-bottom: 30px;
`;

const SkillHobbySection = styled.div`
  flex: 1;
`;

const InputWithButton = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
  margin-bottom: 10px;
`;

const RemoveButton = styled.button`
  height: 45px;
  width: 45px;
  border-radius: 8px;
  background: #E9191D;
  color: white;
  border: none;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  font-weight: bold;
`;

const FormContainer = styled.div`
  position: relative;
  background: rgba(255, 255, 255, 1.00);
  border-radius: 40px;
  box-shadow: 0px 10px 40px rgba(0, 0, 0, 0.15);
  padding: 40px;
  margin-top: 20px;
  margin-bottom: 40px;
  min-height: 500px;
`;

const ResumeGenerationPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    dob: '',
    address: '',
    postalCode: '',
    profile: '',
    education: [{
      institution: '',
      degree: '',
      graduationDate: ''
    }],
    workExperience: [{
      companyName: '',
      jobTitle: '',
      date: '',
      experience: ''
    }],
    skills: [''],
    hobbies: ['']
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleArrayInputChange = (index, value, field) => {
    setFormData(prev => ({
      ...prev,
      [field]: prev[field].map((item, i) => i === index ? value : item)
    }));
  };

  const addArrayItem = (field) => {
    setFormData(prev => ({
      ...prev,
      [field]: [...prev[field], '']
    }));
  };

  const removeArrayItem = (index, field) => {
    setFormData(prev => ({
      ...prev,
      [field]: prev[field].filter((_, i) => i !== index)
    }));
  };

  const addWorkExperience = () => {
    setFormData(prev => ({
      ...prev,
      workExperience: [...prev.workExperience, {
        companyName: '',
        jobTitle: '',
        date: '',
        experience: ''
      }]
    }));
  };

  const removeWorkExperience = (index) => {
    setFormData(prev => ({
      ...prev,
      workExperience: prev.workExperience.filter((_, i) => i !== index)
    }));
  };

  const handleWorkExperienceChange = (index, field, value) => {
    setFormData(prev => ({
      ...prev,
      workExperience: prev.workExperience.map((exp, i) => 
        i === index ? { ...exp, [field]: value } : exp
      )
    }));
  };

  const addEducation = () => {
    setFormData(prev => ({
      ...prev,
      education: [...prev.education, {
        institution: '',
        degree: '',
        graduationDate: ''
      }]
    }));
  };

  const removeEducation = (index) => {
    setFormData(prev => ({
      ...prev,
      education: prev.education.filter((_, i) => i !== index)
    }));
  };

  const handleEducationChange = (index, field, value) => {
    setFormData(prev => ({
      ...prev,
      education: prev.education.map((edu, i) => 
        i === index ? { ...edu, [field]: value } : edu
      )
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/generate-resume', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        alert(errorData.message || 'Failed to generate resume.');
        return;
      }

      // Get the filename from the Content-Disposition header
      const disposition = response.headers.get('Content-Disposition');
      let filename = 'resume.pdf';
      if (disposition && disposition.indexOf('filename=') !== -1) {
        filename = disposition.split('filename=')[1].replace(/"/g, '');
      }

      // Download the PDF
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      alert('An error occurred while generating the resume.');
      console.error(error);
    }
  };

  return (
    <PageContainer>
      <Header>
        <Logo>
          <LogoText>Job</LogoText>
          <LogoText font="Matura MT Script Capitals" size="32px" weight="400">Fiesta</LogoText>
        </Logo>
        
        <Navigation>
          <NavItem active>Home</NavItem>
          <NavItem>Jobs</NavItem>
          <NavItem>Categories</NavItem>
        </Navigation>
        
        <BrowseJobsButton>Browse jobs</BrowseJobsButton>
      </Header>

      <FormSection>
        <FormTitle>Resume Generation</FormTitle>
        <FormSubtitle>Your Gateway to a Perfecto Resume</FormSubtitle>

        <FormContainer>
          <form onSubmit={handleSubmit}>
            <InputColumn>
              <FormGroup>
                <Label>Full name<Required>*</Required></Label>
                <Input
                  type="text"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleInputChange}
                  placeholder="Enter your full name"
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>Email ID<Required>*</Required></Label>
                <Input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email id"
                  required
                />
              </FormGroup>
            </InputColumn>
            <FormImage src="/assets/images/group_27.svg" alt="Resume Generation" />

            <FormGroup>
              <Label>Personal Details<Required>*</Required></Label>
              <InputRow>
                <Input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  placeholder="Enter your phone number"
                  style={{ flex: 1 }}
                />
                <Input
                  type="date"
                  name="dob"
                  value={formData.dob}
                  onChange={handleInputChange}
                  placeholder="Enter Date of Birth"
                  style={{ flex: 1 }}
                />
              </InputRow>
              <InputRow>
                <Input
                  type="text"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  placeholder="Enter your address"
                  style={{ flex: 2 }}
                />
                <Input
                  type="text"
                  name="postalCode"
                  value={formData.postalCode}
                  onChange={handleInputChange}
                  placeholder="Enter Postal Code"
                  style={{ flex: 1 }}
                />
              </InputRow>
            </FormGroup>

            <FormGroup>
              <Label>Profile<Required>*</Required></Label>
              <InputRow>
                <TextArea
                  name="profile"
                  value={formData.profile}
                  onChange={handleInputChange}
                  placeholder="Write a brief description about yourself"
                  style={{ height: '120px', width: '100%' }}
                />
              </InputRow>
            </FormGroup>

            <FormGroup>
              <Label>Education<Required>*</Required></Label>
              {formData.education.map((edu, index) => (
                <div key={index}>
                  <InputRow>
                    <Input
                      type="text"
                      value={edu.institution}
                      onChange={(e) => handleEducationChange(index, 'institution', e.target.value)}
                      placeholder="Institution Name"
                      style={{ flex: 1 }}
                    />
                    {index > 0 && (
                      <RemoveButton onClick={() => removeEducation(index)}>
                        ×
                      </RemoveButton>
                    )}
                  </InputRow>
                  <InputRow>
                    <Input
                      type="text"
                      value={edu.degree}
                      onChange={(e) => handleEducationChange(index, 'degree', e.target.value)}
                      placeholder="Degree Earned"
                      style={{ flex: 1 }}
                    />
                    <Input
                      type="date"
                      value={edu.graduationDate}
                      onChange={(e) => handleEducationChange(index, 'graduationDate', e.target.value)}
                      placeholder="Graduation Date"
                      style={{ flex: 1 }}
                    />
                  </InputRow>
                </div>
              ))}
              <Button type="button" onClick={addEducation}>Education +</Button>
            </FormGroup>

            <FormGroup>
              <Label>Work Experience<Required>*</Required></Label>
              {formData.workExperience.map((exp, index) => (
                <div key={index}>
                  <InputRow>
                    <Input
                      type="text"
                      value={exp.companyName}
                      onChange={(e) => handleWorkExperienceChange(index, 'companyName', e.target.value)}
                      placeholder="Company Name"
                      style={{ flex: 1 }}
                    />
                    {index > 0 && (
                      <RemoveButton onClick={() => removeWorkExperience(index)}>
                        ×
                      </RemoveButton>
                    )}
                  </InputRow>
                  <InputRow>
                    <Input
                      type="text"
                      value={exp.jobTitle}
                      onChange={(e) => handleWorkExperienceChange(index, 'jobTitle', e.target.value)}
                      placeholder="Job Title"
                      style={{ flex: 1 }}
                    />
                    <Input
                      type="date"
                      value={exp.date}
                      onChange={(e) => handleWorkExperienceChange(index, 'date', e.target.value)}
                      placeholder="Date"
                      style={{ flex: 1 }}
                    />
                  </InputRow>
                  <InputRow>
                    <TextArea
                      value={exp.experience}
                      onChange={(e) => handleWorkExperienceChange(index, 'experience', e.target.value)}
                      placeholder="Describe your work experience"
                      style={{ width: '100%' }}
                    />
                  </InputRow>
                </div>
              ))}
              <Button type="button" onClick={addWorkExperience}>Experience +</Button>
            </FormGroup>

            <SkillHobbyContainer>
              <SkillHobbySection>
                <Label>Skills<Required>*</Required></Label>
                {formData.skills.map((skill, index) => (
                  <InputWithButton key={index}>
                    <Input
                      type="text"
                      value={skill}
                      onChange={(e) => handleArrayInputChange(index, e.target.value, 'skills')}
                      placeholder={`Enter Skill ${index + 1}`}
                    />
                    {index > 0 && (
                      <RemoveButton onClick={() => removeArrayItem(index, 'skills')}>
                        ×
                      </RemoveButton>
                    )}
                  </InputWithButton>
                ))}
                <Button type="button" onClick={() => addArrayItem('skills')}>Skill +</Button>
              </SkillHobbySection>

              <SkillHobbySection>
                <Label>Hobbies<Required>*</Required></Label>
                {formData.hobbies.map((hobby, index) => (
                  <InputWithButton key={index}>
                    <Input
                      type="text"
                      value={hobby}
                      onChange={(e) => handleArrayInputChange(index, e.target.value, 'hobbies')}
                      placeholder={`Enter Hobby ${index + 1}`}
                    />
                    {index > 0 && (
                      <RemoveButton onClick={() => removeArrayItem(index, 'hobbies')}>
                        ×
                      </RemoveButton>
                    )}
                  </InputWithButton>
                ))}
                <Button type="button" onClick={() => addArrayItem('hobbies')}>Hobby +</Button>
              </SkillHobbySection>
            </SkillHobbyContainer>

            <ButtonGroup>
              <Button type="button" className="secondary" onClick={() => navigate("/")}>
                Cancel
              </Button>
              <Button type="submit" className="primary">
                Download
              </Button>
            </ButtonGroup>
          </form>
        </FormContainer>
      </FormSection>
    </PageContainer>
  );
};

export default ResumeGenerationPage;
