import { useLocation, useNavigate } from "react-router-dom";
import React from "react";
import styled from "styled-components";

const PageContainer = styled.div`
  position: relative;
  overflow: hidden;
  background: #FFFFFF;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  min-height: 100vh;
  width: 100%;
`;

const Header = styled.div`
  position: absolute;
  background: #F2FFF2;
  height: 89px;
  width: 100%;
  top: 0;
  left: 0;
`;

const Logo = styled.div`
  position: absolute;
  left: 40px;
  top: 29px;
  display: flex;
  align-items: center;
  gap: 5px;
`;

const LogoText = styled.span`
  font-family: ${props => props.font || 'Martel'};
  font-size: ${props => props.size || '24px'};
  font-weight: ${props => props.weight || '900'};
  color: #000000;
`;

const Navigation = styled.div`
  position: absolute;
  height: 50px;
  width: 359.72px;
  left: 50%;
  transform: translateX(-50%);
  top: 27px;
  display: flex;
  gap: 20px;
`;

const NavItem = styled.div`
  padding: 10px;
  cursor: pointer;
  font-family: Inter;
  font-size: 20px;
  font-weight: ${props => props.active ? '700' : '500'};
  color: #282828;
`;

const BrowseJobsButton = styled.div`
  position: absolute;
  right: 40px;
  top: 27px;
  padding: 10px;
  cursor: pointer;
  font-family: Inter;
  font-size: 20px;
  font-weight: 700;
  color: #0C463B;
`;

const ContentSection = styled.div`
  margin-top: 120px;
  padding: 20px;
  max-width: 1200px;
  margin-left: auto;
  margin-right: auto;
`;

const PreviewContainer = styled.div`
  background: #FFFFFF;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 40px;
  margin-top: 20px;
`;

const PreviewHeader = styled.div`
  text-align: center;
  margin-bottom: 30px;
`;

const PreviewName = styled.h1`
  font-family: Poppins;
  font-size: 32px;
  font-weight: 600;
  color: #303030;
  margin-bottom: 10px;
`;

const PreviewContact = styled.div`
  font-family: Poppins;
  font-size: 16px;
  color: #5E6670;
  margin-bottom: 5px;
`;

const PreviewSection = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  font-family: Poppins;
  font-size: 24px;
  font-weight: 600;
  color: #0C463B;
  margin-bottom: 15px;
  border-bottom: 2px solid #F2FFF2;
  padding-bottom: 5px;
`;

const SectionContent = styled.div`
  font-family: Poppins;
  font-size: 16px;
  color: #303030;
  line-height: 1.6;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  margin-top: 40px;
`;

const Button = styled.button`
  height: 45px;
  width: 200px;
  border-radius: 8px;
  font-family: Inter;
  font-size: 18px;
  font-weight: 700;
  cursor: pointer;
  border: none;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  
  &.primary {
    background: #0C463B;
    color: #FFFFFF;
  }
  
  &.secondary {
    background: #E9191D;
    color: #FFFFFF;
  }
`;

const ResumePreviewPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { formData, template } = location.state || {};

  // If no data is passed, redirect back to resume generation
  React.useEffect(() => {
    if (!formData || !template) {
      navigate('/');
    }
  }, [formData, template, navigate]);

  if (!formData || !template) {
    return null;
  }

  return (
    <PageContainer>
      <Header>
        <Logo>
          <LogoText>Job</LogoText>
          <LogoText font="Matura MT Script Capitals" size="32px" weight="400">Fiesta</LogoText>
        </Logo>
        
        <Navigation>
          <NavItem active>Home</NavItem>
          <NavItem>Jobs</NavItem>
          <NavItem>Categories</NavItem>
        </Navigation>
        
        <BrowseJobsButton>Browse jobs</BrowseJobsButton>
      </Header>

      <ContentSection>
        <PreviewContainer>
          <PreviewHeader>
            <PreviewName>{formData.fullName}</PreviewName>
            <PreviewContact>{formData.email} | {formData.phone}</PreviewContact>
            <PreviewContact>{formData.address}, {formData.postalCode}</PreviewContact>
          </PreviewHeader>

          <PreviewSection>
            <SectionTitle>Profile</SectionTitle>
            <SectionContent>{formData.profile}</SectionContent>
          </PreviewSection>

          <PreviewSection>
            <SectionTitle>Education</SectionTitle>
            {formData.education.map((edu, index) => (
              <SectionContent key={index}>
                <strong>{edu.institution}</strong>
                <p>{edu.degree} - {edu.graduationDate}</p>
              </SectionContent>
            ))}
          </PreviewSection>

          <PreviewSection>
            <SectionTitle>Work Experience</SectionTitle>
            {formData.workExperience.map((exp, index) => (
              <SectionContent key={index}>
                <strong>{exp.companyName}</strong> - {exp.jobTitle}
                <p>{exp.date}</p>
                <p>{exp.experience}</p>
              </SectionContent>
            ))}
          </PreviewSection>

          <PreviewSection>
            <SectionTitle>Skills</SectionTitle>
            <SectionContent>
              {formData.skills.join(', ')}
            </SectionContent>
          </PreviewSection>

          <PreviewSection>
            <SectionTitle>Hobbies</SectionTitle>
            <SectionContent>
              {formData.hobbies.join(', ')}
            </SectionContent>
          </PreviewSection>
        </PreviewContainer>

        <ButtonGroup>
          <Button 
            type="button" 
            className="secondary" 
            onClick={() => navigate("/template-selection", { state: { formData } })}
          >
            Back
          </Button>
          <Button 
            type="button" 
            className="primary" 
            onClick={() => {
              // Here you would typically handle the final resume generation
              console.log('Generating resume with template:', template);
            }}
          >
            Download Resume
          </Button>
        </ButtonGroup>
      </ContentSection>
    </PageContainer>
  );
};

export default ResumePreviewPage;
